import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getFromLocalStorage, verifySignature } from '../utils/cryptoUtils';

const VerifyAgePage: React.FC = () => {
  const [did, setDid] = useState<string>('');
  const [firstName, setFirstName] = useState<string>('');
  const [lastName, setLastName] = useState<string>('');
  const [isOver18, setIsOver18] = useState<boolean | null>(null);
  const [verificationRequested, setVerificationRequested] = useState<boolean>(false);
  const [verificationResult, setVerificationResult] = useState<boolean | null>(null);
  const [showDetails, setShowDetails] = useState<boolean>(false);

  useEffect(() => {
    // LocalStorage'dan bilgileri al
    const storedDid = getFromLocalStorage('did');
    const storedFirstName = getFromLocalStorage('firstName');
    const storedLastName = getFromLocalStorage('lastName');
    const storedIsOver18 = getFromLocalStorage('isOver18');

    if (storedDid) setDid(storedDid);
    if (storedFirstName) setFirstName(storedFirstName);
    if (storedLastName) setLastName(storedLastName);
    if (storedIsOver18 !== null) setIsOver18(storedIsOver18);
  }, []);

  const handleRequestVerification = () => {
    setVerificationRequested(true);

    // Gerçek bir sistemde, burada bir doğrulayıcıya istek gönderilir
    // Simülasyon amaçlı 1.5 saniye bekletiyoruz
    setTimeout(() => {
      // Doğrulayıcı, kişinin 18 yaşından büyük olup olmadığını kontrol eder
      // Gerçek doğum tarihini görmeden, sadece isOver18 değerini doğrular
      setVerificationResult(isOver18);
    }, 1500);
  };

  return (
    <div className="animate-fade-in">
      <h1 className="heading text-center text-3xl mb-8">Yaş Doğrulama Örneği</h1>

      <div className="max-w-3xl mx-auto">
        <div className="step-card">
          <h2 className="subheading">Sıfır Bilgi İspatı ile Yaş Doğrulama</h2>
          <div className="prose">
            <p className="mb-4">
              Bu örnek, dijital kimliğinizi kullanarak yaşınızı doğrulamayı göstermektedir. 
              Önemli olan nokta, <strong>gerçek doğum tarihinizi paylaşmadan</strong> sadece 
              18 yaşından büyük olup olmadığınızı kanıtlayabilmenizdir.
            </p>
            <p className="mb-4">
              Bu, "Sıfır Bilgi İspatı" (Zero-Knowledge Proof) olarak adlandırılan bir kriptografik 
              tekniktir ve gizliliğinizi korurken gerekli bilgileri doğrulamanıza olanak tanır.
            </p>
          </div>
        </div>

        <div className="step-card">
          <h2 className="subheading">Kimlik Bilgileriniz</h2>

          {did ? (
            <div className="did-card mb-6">
              <div className="did-card-inner">
                <div className="did-card-chip"></div>
                <div className="mb-4">
                  <div className="text-xs opacity-70">Dijital Kimlik (DID)</div>
                  <div className="text-xl font-mono break-all">{did}</div>
                </div>
                <div className="mb-2">
                  <div className="text-xs opacity-70">Kimlik Sahibi</div>
                  <div className="text-sm">{firstName} {lastName}</div>
                </div>
                <div className="flex justify-between items-end">
                  <div className="text-xs opacity-70">Doğrulama Durumu</div>
                  <div className="text-sm">
                    {verificationResult === null ? 'Doğrulanmadı' : 
                     verificationResult ? 'Yaş Doğrulandı (18+)' : 'Yaş Doğrulanamadı (18-)'}
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className="alert alert-warning">
              <p>Henüz bir dijital kimliğiniz yok. Lütfen önce kayıt olun.</p>
              <div className="mt-4">
                <Link to="/register" className="btn btn-primary px-6 py-2">
                  Kayıt Sayfasına Git
                </Link>
              </div>
            </div>
          )}

          {did && !verificationRequested && (
            <div className="text-center py-4">
              <button 
                onClick={handleRequestVerification} 
                className="btn btn-primary px-6 py-2"
              >
                Yaş Doğrulaması İste
              </button>
              <p className="text-sm text-gray-500 mt-2">
                Bu işlem, doğum tarihinizi paylaşmadan 18 yaşından büyük olduğunuzu doğrulayacaktır.
              </p>
            </div>
          )}

          {verificationRequested && verificationResult === null && (
            <div className="text-center py-4">
              <div className="flex items-center justify-center">
                <svg className="animate-spin h-8 w-8 text-primary-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                <span className="ml-2">Doğrulama İşlemi Yapılıyor...</span>
              </div>
            </div>
          )}

          {verificationResult !== null && (
            <div className={`alert ${verificationResult ? 'alert-success' : 'alert-danger'} animate-slide-in`}>
              {verificationResult ? (
                <div>
                  <p className="font-bold">Doğrulama Başarılı!</p>
                  <p>
                    {firstName} {lastName} adlı kişinin 18 yaşından büyük olduğu doğrulandı.
                    <br />
                    <span className="text-sm opacity-80">Not: Bu doğrulama, kişinin tam doğum tarihini ifşa etmeden yapıldı.</span>
                  </p>
                </div>
              ) : (
                <div>
                  <p className="font-bold">Doğrulama Başarısız!</p>
                  <p>
                    {firstName} {lastName} adlı kişinin 18 yaşından büyük olduğu doğrulanamadı.
                    <br />
                    <span className="text-sm opacity-80">Not: Bu doğrulama, kişinin tam doğum tarihini ifşa etmeden yapıldı.</span>
                  </p>
                </div>
              )}

              <div className="mt-4">
                <button 
                  onClick={() => setShowDetails(!showDetails)} 
                  className="text-sm underline"
                >
                  {showDetails ? 'Teknik Detayları Gizle' : 'Teknik Detayları Göster'}
                </button>

                {showDetails && (
                  <div className="mt-2 p-3 bg-gray-100 rounded text-sm">
                    <p className="font-mono">
                      <strong>Doğrulama Süreci:</strong><br />
                      1. Kullanıcı, doğum tarihini içeren kimlik bilgilerini oluşturdu.<br />
                      2. Sistem, doğum tarihinden yaş hesaplaması yaptı ve "isOver18" değerini belirledi.<br />
                      3. Doğrulayıcı, kullanıcının gerçek doğum tarihini görmeden sadece "isOver18" değerini doğruladı.<br />
                      4. Bu sayede kullanıcı, doğum tarihini ifşa etmeden yaşının 18'den büyük olduğunu kanıtladı.
                    </p>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        <div className="step-card">
          <h2 className="subheading">Sıfır Bilgi İspatı Nedir?</h2>
          <div className="prose">
            <p>
              Sıfır Bilgi İspatı (Zero-Knowledge Proof), bir tarafın diğer tarafa, belirli bir bilginin 
              doğruluğunu kanıtlamasına olanak tanıyan, ancak o bilginin kendisi hakkında başka hiçbir 
              bilgi vermeden bunu yapabildiği kriptografik bir yöntemdir.
            </p>
            <p>
              Örneğin bu örnekte, doğum tarihinizi paylaşmadan sadece 18 yaşından büyük olduğunuzu 
              kanıtlayabilirsiniz. Bu, gizliliğinizi korurken gerekli doğrulamaları yapmanıza olanak tanır.
            </p>
            <p>
              Gerçek dünyada bu teknoloji, kimlik doğrulama, yaş doğrulama, finansal işlemler ve 
              daha birçok alanda kullanılabilir.
            </p>
          </div>

          <div className="mt-6 text-center">
            <Link to="/" className="btn btn-secondary px-6 py-2">
              Ana Sayfaya Dön
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VerifyAgePage;
