import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getFromLocalStorage, signMessage, verifySignature, verifyAgeCredential } from '../utils/cryptoUtils';

const LoginPage: React.FC = () => {
  const navigate = useNavigate();
  const [did, setDid] = useState<string>('');
  const [keyPair, setKeyPair] = useState<any>(null);
  const [signature, setSignature] = useState<string>('');
  const [isVerifying, setIsVerifying] = useState<boolean>(false);
  const [isVerified, setIsVerified] = useState<boolean>(false);
  const [verificationError, setVerificationError] = useState<string>('');
  const [currentStep, setCurrentStep] = useState<number>(0);
  const [showAnimation, setShowAnimation] = useState<boolean>(false);
  const [ageVerified, setAgeVerified] = useState<boolean>(false);
  const [userData, setUserData] = useState<any>(null);

  // Sabit mesaj
  const loginMessage = "DID ile Giri≈ü Yapƒ±yorum";

  // LocalStorage'dan DID, anahtar √ßifti ve kullanƒ±cƒ± verilerini kontrol et
  useEffect(() => {
    const storedDid = getFromLocalStorage('did');
    const storedKeyPair = getFromLocalStorage('didKeyPair');
    const storedUserData = getFromLocalStorage('userData');
    const storedCredential = getFromLocalStorage('userCredential');

    if (!storedDid || !storedKeyPair) {
      // DID veya anahtar √ßifti yoksa kayƒ±t sayfasƒ±na y√∂nlendir
      navigate('/register');
      return;
    }

    setDid(storedDid);
    setKeyPair(storedKeyPair);

    if (storedUserData) {
      setUserData(storedUserData);
    }

    if (storedCredential) {
      // Ya≈ü doƒürulama bilgisini al ama doƒüum tarihini alma
      setAgeVerified(storedCredential.isAdult);
    }
  }, [navigate]);

  const handleSignMessage = () => {
    try {
      // ƒ∞mza olu≈ütur
      const signatureHex = signMessage(loginMessage, keyPair.privateKey);
      setSignature(signatureHex);
      setCurrentStep(1);
    } catch (error) {
      console.error('ƒ∞mza olu≈üturma hatasƒ±:', error);
      setVerificationError('ƒ∞mza olu≈üturulurken bir hata olu≈ütu. L√ºtfen tekrar deneyin.');
    }
  };

  const handleVerifySignature = () => {
    setIsVerifying(true);
    setShowAnimation(true);

    // Animasyon i√ßin kƒ±sa bir gecikme
    setTimeout(() => {
      try {
        // ƒ∞mzayƒ± doƒürula
        const isValid = verifySignature(loginMessage, signature, keyPair.publicKey);

        if (isValid) {
          setIsVerified(true);
          setVerificationError('');

          // Ya≈ü doƒürulama kimlik bilgisini doƒürula
          const credential = getFromLocalStorage('userCredential');
          if (credential) {
            const isCredentialValid = verifyAgeCredential(credential, keyPair.publicKey);
            if (isCredentialValid) {
              setAgeVerified(credential.isAdult);

              // 18 ya≈ü kontrol√º
              if (!credential.isAdult) {
                // 18 ya≈üƒ±ndan k√º√ß√ºkse hata ver
                setVerificationError('Giri≈ü yapƒ±lamadƒ±! Bu uygulama i√ßin 18 ya≈üƒ±ndan b√ºy√ºk olmanƒ±z gerekmektedir.');
                setCurrentStep(1); // ƒ∞mza doƒürulama adƒ±mƒ±nda kal
              } else {
                // 18 ya≈üƒ±ndan b√ºy√ºkse devam et
                setCurrentStep(2);
                // Ba≈üarƒ±lƒ± giri≈ü bilgisini kaydet
                localStorage.setItem('lastLogin', new Date().toISOString());
              }
            } else {
              console.error('Ya≈ü doƒürulama kimlik bilgisi ge√ßersiz');
              setVerificationError('Ya≈ü doƒürulama kimlik bilgisi ge√ßersiz. L√ºtfen tekrar kayƒ±t olun.');
            }
          } else {
            setVerificationError('Ya≈ü doƒürulama kimlik bilgisi bulunamadƒ±. L√ºtfen tekrar kayƒ±t olun.');
            setCurrentStep(1);
          }
        } else {
          setIsVerified(false);
          setVerificationError('ƒ∞mza doƒürulanamadƒ±. L√ºtfen tekrar deneyin.');
        }
      } catch (error) {
        console.error('ƒ∞mza doƒürulama hatasƒ±:', error);
        setIsVerified(false);
        setVerificationError('ƒ∞mza doƒürulanƒ±rken bir hata olu≈ütu. L√ºtfen tekrar deneyin.');
      }

      setIsVerifying(false);

      // Animasyonu bir s√ºre daha g√∂ster
      setTimeout(() => {
        setShowAnimation(false);
      }, 1000);
    }, 2000);
  };

  return (
    <div className="animate-fade-in">
      <h1 className="heading text-center text-3xl mb-8">DID ile Giri≈ü Yapma</h1>
      <p className="text-center font-semibold text-lg mb-4">
        "Bir bilgiyi bildiƒüini ispatla, ama bana o bilgiyi g√∂sterme!"
      </p>

      <div className="max-w-3xl mx-auto">
        <div className="step-card">
          <h2 className="subheading flex items-center">
            <span className="step-number">1</span>
            ƒ∞mza ile Kimlik Doƒürulama
          </h2>
          <div className="prose">
            <p className="mb-4">
              DID sistemlerinde kimlik doƒürulama, √∂zel anahtarƒ±nƒ±zla bir mesajƒ± imzalayarak ger√ßekle≈ütirilir.
              Bu imza, genel anahtarƒ±nƒ±z (Verkey) kullanƒ±larak doƒürulanabilir.
            </p>
            <div className="border border-gray-200 rounded-lg p-4 mb-4 bg-white">
  <h3 className="font-bold text-lg mb-4">Melike ≈ûifreyi Biliyor mu?</h3>

  <div className="bg-blue-50 p-4 rounded-lg mb-6">
    <p className="font-semibold mb-2">üé¨ Karakterler:</p>
    <ul className="list-disc pl-5">
      <li><strong>Enes:</strong> Doƒürulayƒ±cƒ± ‚Äì sadece "evet" ya da "hayƒ±r" cevabƒ±nƒ± ister.</li>
      <li><strong>Melike:</strong> Bilgi sahibi ‚Äì gizli bir bilgiyi (≈üifreyi) bildiƒüini ispatlamak istiyor ama ≈üifreyi s√∂ylemeden.</li>
    </ul>
  </div>

  <div className="bg-yellow-50 p-4 rounded-lg mb-6">
    <p className="font-semibold mb-2">üîê Durum: Gizli Kapƒ± ve ≈ûifre</p>
    <p>Melike, ≈üifreli bir kapƒ±nƒ±n olduƒüu bir odaya gider. Bu kapƒ±yƒ± a√ßmak i√ßin √∂zel bir ≈üifre gerekir.</p>
    <p className="mt-2">üí¨ Enes: "Eƒüer ≈üifreyi ger√ßekten biliyorsan, kapƒ±nƒ±n diƒüer tarafƒ±ndan geri √ßƒ±kabilmelisin!"</p>
  </div>

  <div className="border border-gray-300 rounded-lg p-4 mb-6">
    <div className="flex justify-center mb-4">
      <div className="relative">
        <img src="/images/Adsƒ±z tasarƒ±m.png" alt="Zero Knowledge Proof Diagram" className="max-w-full h-auto" />
      </div>
    </div>
  </div>

  <div className="bg-green-50 p-4 rounded-lg">
    <p className="font-semibold mb-2">‚úÖ Sonu√ß: Melike Ger√ßekten ≈ûifreyi Biliyor ama ≈ûifreyi Hi√ß S√∂ylemiyor!</p>
    <p>Enes, Melike'nin kapƒ±yƒ± a√ßabildiƒüini g√∂r√ºr ‚Üí "≈ûifreyi bildiƒüinden emin olur."</p>
    <p className="font-semibold mt-2">Ama Melike'nin ≈üifresinin ne olduƒüunu hi√ß √∂ƒürenmez.</p>
  </div>
</div>


            <p>
              Bu adƒ±mda, "<strong>{loginMessage}</strong>" mesajƒ±nƒ± √∂zel anahtarƒ±nƒ±zla imzalayarak 
              kimliƒüinizi doƒürulayacaksƒ±nƒ±z.
            </p>
          </div>
        </div>

        <div className="step-card">
          <h2 className="subheading flex items-center">
            <span className="step-number">2</span>
            Kimliƒüinizi Doƒürulayƒ±n
          </h2>

          <div className="bg-gray-50 border border-gray-200 rounded-md p-4 mb-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="label">DID</label>
                <div className="input bg-gray-100 overflow-x-auto whitespace-nowrap font-mono text-sm">
                  {did}
                </div>
              </div>
              <div>
                <label className="label">Mesaj</label>
                <div className="input bg-gray-100 overflow-x-auto whitespace-nowrap font-mono text-sm">
                  {loginMessage}
                </div>
              </div>
            </div>
          </div>

          {currentStep === 0 && (
            <div className="text-center">
              <button 
                onClick={handleSignMessage} 
                className="btn btn-primary px-6 py-2"
              >
                Mesajƒ± ƒ∞mzala
              </button>
            </div>
          )}

          {currentStep >= 1 && (
            <div className="animate-slide-in mt-6">
              <h3 className="text-lg font-semibold mb-2">ƒ∞mza Olu≈üturuldu</h3>
              <div className="bg-gray-50 border border-gray-200 rounded-md p-4 mb-4">
                <label className="label">ƒ∞mza (Hex)</label>
                <div className="input bg-gray-100 overflow-x-auto whitespace-nowrap font-mono text-xs">
                  {signature}
                </div>
              </div>

              {currentStep === 1 && (
                <div className="text-center">
                  <button 
                    onClick={handleVerifySignature} 
                    disabled={isVerifying}
                    className={`btn btn-primary px-6 py-2 ${isVerifying ? 'opacity-70 cursor-not-allowed' : ''}`}
                  >
                    {isVerifying ? 'Doƒürulanƒ±yor...' : 'ƒ∞mzayƒ± Doƒürula'}
                  </button>
                </div>
              )}

              {verificationError && (
                <div className="error-box mt-4">
                  <p>{verificationError}</p>
                </div>
              )}
            </div>
          )}

          {showAnimation && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-8 rounded-lg shadow-xl max-w-md w-full">
                <div className="text-center">
                  <div className="w-24 h-24 mx-auto mb-4 relative">
                    <div className="absolute inset-0 border-4 border-primary-200 border-t-primary-600 rounded-full animate-spin"></div>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-12 w-12 text-primary-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                      </svg>
                    </div>
                  </div>
                  <h3 className="text-xl font-bold mb-2">ƒ∞mza Doƒürulanƒ±yor</h3>
                  <p className="text-gray-600">
                    ƒ∞mzanƒ±z genel anahtarƒ±nƒ±z (Verkey) kullanƒ±larak doƒürulanƒ±yor...
                  </p>
                </div>
              </div>
            </div>
          )}

          {currentStep === 2 && (
            <div className="animate-slide-in mt-6">
              <div className="success-box">
                <div className="flex items-center">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-green-600 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  <span className="font-semibold">Kimliƒüiniz ba≈üarƒ±yla doƒürulandƒ±!</span>
                </div>
                <p className="text-sm mt-2">
                  ƒ∞mzanƒ±z ge√ßerli ve DID'iniz <span className="text-highlight">{did}</span> ile e≈üle≈üiyor.
                  Artƒ±k sisteme giri≈ü yaptƒ±nƒ±z.
                </p>
              </div>

              {/* Ya≈ü doƒürulama bilgisini g√∂ster */}
              <div className="did-card mb-6 mt-6">
                <div className="did-card-inner">
                  <div className="did-card-chip"></div>
                  <div className="mb-4">
                    <div className="text-xs opacity-70">Ad Soyad</div>
                    <div className="text-xl">{userData?.firstName} {userData?.lastName}</div>
                  </div>
                  <div className="mb-4">
                    <div className="text-xs opacity-70">Kimlik Doƒürulama</div>
                    <div className="text-sm font-semibold text-green-600">
                      {ageVerified ? '18 Ya≈ü √úst√º Doƒürulandƒ±' : '18 Ya≈ü Altƒ±'}
                    </div>
                  </div>
                  <div className="flex justify-between items-end">
                    <div className="text-xs opacity-70">DID</div>
                    <div className="text-sm font-mono">{did.substring(0, 16)}...</div>
                  </div>
                </div>
              </div>

              <div className="info-box">
                <p className="font-medium">Ya≈ü doƒürulama kimliƒüiniz ba≈üarƒ±yla doƒürulandƒ±!</p>
                <p className="text-sm mt-1">
                  Doƒüum tarihiniz sistemde saklanmadan, sadece ya≈üƒ±nƒ±zƒ±n 18'den b√ºy√ºk olup olmadƒ±ƒüƒ± bilgisi doƒürulandƒ±.
                </p>
              </div>

              <div className="mt-6 text-center">
                <Link to="/learned" className="btn btn-primary px-6 py-2">
                  Devam Et: √ñƒürendiklerinizi G√∂zden Ge√ßirin
                </Link>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
